// api.js
import axios from 'axios';
import router from '@/router/index';
import { ENDPOINTS, ROUTESNAMES, notify } from '@/helpers';

// Function to make a generic HTTP request
export async function callApi({ method, url, body = {}, headers = {}, withFiles = false }) {
  if (!url || !method) {
    console.error('Both the URL and the method must be provided.');
    returnPromise.reject({
      data:{
        message:'Both the URL and the method must be provided.'
      }
    })
  }
  // check network
  if (!navigator.onLine) {
    // notify({ type: 'error', msg: 'not_online' });
    console.error('not_online');
    return Promise.reject({
      data:{
        message:'not_online'
      }
    });
  }

  return axios({
    method,
    url,
    ...(body && { data: body }),
    headers: {
      Accept: 'application/json',
      Platform: 'web',
      'Content-Type': withFiles ? 'multipart/form-data' : 'application/json',
      ...headers,
    },
  })
    .then((response) => response.data)
    .catch((error) => {
      const errorResponse=error.response
      switch (errorResponse?.status) {
        case 404: {
          // router.push(ROUTESNAMES.errors._404);
          break;
        }
        default:
          () => {};
      }
      return Promise.reject(errorResponse);
    });
}

export function useCallApi(options) {
  const isPending = ref(true);

  const isSuccess = ref(false);
  const data = ref(null);

  const isError = ref(false);
  const error = ref(null);
  const errorData = ref(null);

  {
    (async ()=> {
      try {
        // isPending.value = true;
        const res = await callApi(options);
        if(res?.status){
          isSuccess.value = true;
          data.value = res?.data;
        }else{
          isError.value = true;
          error.value = res?.data;
          errorData.value = res?.data;
        }

      } catch (err) {
        isError.value = true;
        error.value = err;
        errorData.value = err?.data;
      } finally {
        isPending.value = false;
      }
    })();
  }

  return {
    isPending,

    isSuccess,
    data,

    isError,
    error,
    errorData,
  };
}
